mapper.select:
SELECT bkno, bkname, bkwrite, bkimg, bkprice, bkcate, ( SELECT COUNT(*) FROM books
AND bkcate = #{bkcate}
AND bkname LIKE CONCAT('%', #{searchV}, '%')
AND bkwrite LIKE CONCAT('%', #{searchV}, '%')
) AS count FROM books
AND bkcate = #{bkcate}
AND bkname LIKE CONCAT('%', #{searchV}, '%') OR bkwrite LIKE CONCAT('%', #{searchV}, '%')
AND bkname LIKE CONCAT('%', #{searchV}, '%')
AND bkwrite LIKE CONCAT('%', #{searchV}, '%')
ORDER BY bkno asc
